mapper.select:
SELECT 
  FROM vw_capitalsources
 WHERE capitalsourceid = #{id}
   AND maf_id          = #{userId}
   AND mac_id_accessor = #{accessorId}
 LIMIT 1

SELECT 
  FROM vw_capitalsources
 WHERE mac_id_creator  = #{userId}
   AND comment         = #{comment}
   AND #{date}   BETWEEN validfrom AND validtil
 LIMIT 1

SELECT DISTINCT UPPER(SUBSTR(comment,1,1)) letters
  FROM vw_capitalsources
 WHERE maf_id = #{userId}
   AND maf_validfrom  <=  #{validTil}
   AND maf_validtil   >=  #{validFrom}
   AND validfrom  <=  #{validTil}
   AND validtil   >=  #{validFrom}
 ORDER BY letters

SELECT COUNT(*)
  FROM vw_capitalsources
 WHERE maf_id = #{userId}
   AND maf_validfrom  <=  #{validTil}
   AND maf_validtil   >=  #{validFrom}
   AND validfrom  <=  #{validTil}
   AND validtil   >=  #{validFrom}

SELECT 
  FROM vw_capitalsources
 WHERE maf_id = #{userId}
   AND maf_validfrom  <=  #{validTil}
   AND maf_validtil   >=  #{validFrom}
   AND validfrom  <=  #{validTil}
   AND validtil   >=  #{validFrom}
 ORDER BY CASE WHEN  mac_id_creator = #{userId} THEN 1 ELSE 2 END
      ,capitalsourceid

SELECT 
  FROM vw_capitalsources
 WHERE UPPER(comment) LIKE CONCAT(UPPER(#{initial}),'%')
   AND maf_id = #{userId}
   AND maf_validfrom  <=  #{validTil}
   AND maf_validtil   >=  #{validFrom}
   AND validfrom  <=  #{validTil}
   AND validtil   >=  #{validFrom}
 ORDER BY CASE WHEN  mac_id_creator = #{userId} THEN 1 ELSE 2 END
      ,capitalsourceid

mapper.update:
UPDATE capitalsources
   SET type           =	#{type}
      ,state          =	#{state}
      ,accountnumber  =	#{accountNumber}
      ,bankcode       =	#{bankCode}
      ,comment        =	#{comment}
      ,validtil       =	#{validTil}
      ,validfrom      =	#{validFrom}
      ,att_group_use  =	#{attGroupUse}
      ,import_allowed =	#{importAllowed}
 WHERE capitalsourceid = #{id}
   AND mac_id_creator  = #{macIdCreator}
   AND mac_id_accessor = #{macIdAccessor}
 LIMIT 1

mapper.delete:
DELETE
  FROM capitalsources
 WHERE mac_id_creator  = #{userId}
   AND mac_id_accessor = #{accessorId}
   AND capitalsourceid = #{id}
 LIMIT 1

mapper.select:
SELECT 1
  FROM moneyflows
 WHERE mcs_capitalsourceid = #{id}
   AND bookingdate NOT BETWEEN #{validFrom} AND #{validTil}
 LIMIT 1

SELECT 
  FROM vw_capitalsources
 WHERE maf_id = #{userId}
   AND NOW() BETWEEN maf_validfrom AND maf_validtil
   AND ( mac_id_creator = #{userId}
         OR
         att_group_use = 1
       )
 ORDER BY CASE WHEN  mac_id_creator = #{userId} THEN 1 ELSE 2 END
      ,capitalsourceid

SELECT 
  FROM vw_capitalsources
 WHERE maf_id = #{userId}
   AND maf_validfrom  <=  #{validTil}
   AND maf_validtil   >=  #{validFrom}
   AND ( mac_id_creator = #{userId}
         OR
         att_group_use = 1
       )
   AND validfrom  <=  #{validTil}
   AND validtil   >=  #{validFrom}
 ORDER BY CASE WHEN  mac_id_creator = #{userId} THEN 1 ELSE 2 END
      ,capitalsourceid

SELECT 
  FROM capitalsources
 WHERE accountnumber = #{accountNumber}
   AND bankcode      = #{bankCode}
   AND #{date}       BETWEEN validfrom AND validtil
 LIMIT 1
