mapper.select:
SELECT FROM moneyflowsplitentries WHERE mmf_moneyflowid IN
#{moneyflowId}
ORDER BY 2, 1

mapper.update:
UPDATE moneyflowsplitentries
   SET amount                = #{amount}
      ,mpa_postingaccountid  = #{mpaPostingAccountId}
      ,comment               = #{comment}
 WHERE moneyflowsplitentryid = #{id}
 LIMIT 1
