mapper.select:
SELECT

FROM access_relation
WHERE mau_userid = #{id}
AND validtil >= #{date}
ORDER BY validfrom

mapper.delete:
DELETE
  FROM access_relation
 WHERE mau_userid = #{id}
   AND validfrom = #{date}

mapper.update:
UPDATE access_relation
   SET mag_groupid = #{accessRelationData.magGroupId}
     , validfrom   = #{accessRelationData.validFrom}
     , validtil    = #{accessRelationData.validTil}
 WHERE mau_userid = #{id}
   AND validfrom = #{date}

mapper.insert:
INSERT
  INTO access_relation
      ( mau_userid
      , mag_groupid
      , validfrom
      , validtil
      )
VALUES
    ( #{mauUserId}
    , #{magGroupId}
    , #{validFrom}
    , #{validTil}
    )
