mapper.select:
SELECT

FROM vw_capitalsources
WHERE capitalsourceid = #{id}
AND mar_mau_userid = #{userId}
AND mag_groupid = #{magGroupId}
FETCH FIRST 1 ROW ONLY

SELECT

FROM vw_capitalsources
WHERE mau_userid = #{userId}
AND comment = #{comment}
AND #{date} BETWEEN validfrom AND validtil
FETCH FIRST 1 ROW ONLY

SELECT

FROM vw_capitalsources
WHERE mar_mau_userid = #{userId}
AND maf_validfrom  <=  #{validTil}
AND maf_validtil   >=  #{validFrom}
AND validfrom      <=  #{validTil}
AND validtil       >=  #{validFrom}
ORDER BY CASE WHEN mau_userid = #{userId} THEN 1 ELSE 2 END
,type
,capitalsourceid

mapper.insert:
INSERT
  INTO capitalsources
      ( mau_userid
      , mag_groupid
      , type
      , state
      , accountnumber
      , bankcode
      , comment
      , validtil
      , validfrom
      , att_group_use
      , import_allowed
      )
VALUES
    ( #{mauUserId}
    , #{magGroupId}
    , #{type}
    , #{state}
    , #{accountNumber}
    , #{bankCode}
    , #{comment}
    , #{validTil}
    , #{validFrom}
    , #{attGroupUse}
    , #{importAllowed}
    )

mapper.update:
UPDATE capitalsources
   SET type           = #{type}
     , state          = #{state}
     , accountnumber  = #{accountNumber}
     , bankcode       = #{bankCode}
     , comment        = #{comment}
     , validtil       = #{validTil}
     , validfrom      = #{validFrom}
     , att_group_use  = #{attGroupUse}
     , import_allowed = #{importAllowed}
 WHERE capitalsourceid = #{id}
   AND mau_userid = #{mauUserId}
   AND mag_groupid = #{magGroupId}

mapper.delete:
DELETE
  FROM capitalsources
 WHERE mau_userid = #{userId}
   AND mag_groupid = #{magGroupId}
   AND capitalsourceid = #{id}

mapper.select:
SELECT 1
  FROM moneyflows
 WHERE mcs_capitalsourceid = #{id}
   AND bookingdate NOT BETWEEN #{validFrom} AND #{validTil}
     FETCH FIRST 1 ROWS ONLY

SELECT

FROM vw_capitalsources
WHERE mar_mau_userid = #{userId}
AND maf_validfrom  <=  #{validTil}
AND maf_validtil   >=  #{validFrom}
AND ( mau_userid = #{userId}
OR
att_group_use = 1
)
AND validfrom  <=  #{validTil}
AND validtil   >=  #{validFrom}
ORDER BY CASE WHEN mau_userid = #{userId} THEN 1 ELSE 2 END
,capitalsourceid

SELECT

FROM capitalsources
WHERE accountnumber = #{accountNumber}
AND bankcode = #{bankCode}
AND #{date} BETWEEN validfrom AND validtil
