mapper.select:
SELECT

FROM vw_etf
WHERE etfid = #{etfid}
AND mar_mau_userid = #{mauUserId}
AND NOW() BETWEEN maf_validfrom AND maf_validtil

mapper.insert:
INSERT
  INTO etf
      ( mau_userid
      , mag_groupid
      , isin
      , name
      , wkn
      , ticker
      , chart_url
      , trans_cost_abs
      , trans_cost_rel
      , trans_cost_max
      , part_tax_exempt
      )
VALUES
    ( #{mauUserId}
    , #{magGroupId}
    , #{isin}
    , #{name}
    , #{wkn}
    , #{ticker}
    , #{chartUrl}
    , #{transCostAbs}
    , #{transCostRel}
    , #{transCostMax}
    , #{partTaxExempt}
    )

mapper.update:
UPDATE etf
   SET isin            = #{isin}
     , name            = #{name}
     , wkn             = #{wkn}
     , ticker          = #{ticker}
     , chart_url       = #{chartUrl}
     , trans_cost_abs  = #{transCostAbs}
     , trans_cost_rel  = #{transCostRel}
     , trans_cost_max  = #{transCostMax}
     , part_tax_exempt = #{partTaxExempt}
 WHERE etfid = #{etfid}
   AND mag_groupid = #{magGroupId}

mapper.delete:
DELETE
  FROM etf
 WHERE etfid = #{etfid}
   AND mag_groupid = #{magGroupId}

mapper.select:
SELECT

FROM etfflows
WHERE met_etfid = #{metEtfid}
AND flowdate  <=  #{dateUntil}
ORDER BY flowdate DESC

SELECT

FROM etfvalues
WHERE isin = #{isin}
AND date BETWEEN #{startDate} AND #{endDate}
ORDER BY date DESC
FETCH FIRST 1 ROWS ONLY

mapper.insert:
INSERT
  INTO etfflows
      ( met_etfid
      , flowdate
      , amount
      , price
      )
VALUES
    ( #{metEtfid}
    , #{flowdate}
    , #{amount}
    , #{price}
    )

mapper.update:
UPDATE etfflows
   SET flowdate  = #{flowdate}
     , met_etfid = #{metEtfid}
     , amount    = #{amount}
     , price     = #{price}
 WHERE etfflowid = #{etfflowid}

mapper.select:
SELECT etfpreliminarylumpsumid
  FROM etfpreliminarylumpsum
 WHERE met_etfid = #{metEtfid}
   AND "year" = #{year}

mapper.insert:
INSERT
  INTO etfpreliminarylumpsum
      ( met_etfid
      , "year"
      , type
      , amount_per_piece
      , amount01
      , amount02
      , amount03
      , amount04
      , amount05
      , amount06
      , amount07
      , amount08
      , amount09
      , amount10
      , amount11
      , amount12
      )
VALUES
    ( #{metEtfid}
    , #{year}
    , #{type}
    , #{amountPerPiece}
    , #{amountJanuary}
    , #{amountFebruary}
    , #{amountMarch}
    , #{amountApril}
    , #{amountMay}
    , #{amountJune}
    , #{amountJuly}
    , #{amountAugust}
    , #{amountSeptember}
    , #{amountOctober}
    , #{amountNovember}
    , #{amountDecember}
    )

mapper.update:
UPDATE etfpreliminarylumpsum
   SET met_etfid        = #{metEtfid}
     , "year"           = #{year}
     , type             = #{type}
     , amount_per_piece = #{amountPerPiece}
     , amount01         = #{amountJanuary}
     , amount02         = #{amountFebruary}
     , amount03         = #{amountMarch}
     , amount04         = #{amountApril}
     , amount05         = #{amountMay}
     , amount06         = #{amountJune}
     , amount07         = #{amountJuly}
     , amount08         = #{amountAugust}
     , amount09         = #{amountSeptember}
     , amount10         = #{amountOctober}
     , amount11         = #{amountNovember}
     , amount12         = #{amountDecember}
 WHERE etfpreliminarylumpsumid = #{etfPreliminaryLumpSumId}
