mapper.select:
SELECT 
  FROM impmonthlysettlements
 WHERE month  = #{month}
   AND year   = #{year}

mapper.insert:
INSERT
  INTO impmonthlysettlements
      (externalid
      ,mcs_capitalsourceid
      ,month
      ,year
      ,amount
      )
VALUES
      (#{externalId}
      ,#{mcsCapitalsourceId}
      ,#{month}
      ,#{year}
      ,#{amount}
      )
    ON DUPLICATE KEY
UPDATE amount = VALUES(amount)
