mapper.select:
SELECT

FROM vw_moneyflows
WHERE moneyflowid = #{id}
AND mar_mau_userid = #{userId}
AND ( private = 0 OR mau_userid = #{userId} )

mapper.insert:
INSERT
  INTO moneyflows
      ( mau_userid
      , mag_groupid
      , bookingdate
      , invoicedate
      , amount
      , mcs_capitalsourceid
      , mcp_contractpartnerid
      , comment
      , mpa_postingaccountid
      , private
      )
VALUES
    ( #{mauUserId}
    , #{magGroupId}
    , #{bookingdate}
    , #{invoicedate}
    , #{amount}
    , #{mcsCapitalsourceId}
    , #{mcpContractpartnerId}
    , #{comment}
    , #{mpaPostingAccountId}
    , #{privat}
    )

mapper.update:
UPDATE moneyflows
   SET bookingdate           = #{bookingdate}
     , invoicedate           = #{invoicedate}
     , amount                = #{amount}
     , mcs_capitalsourceid   = #{mcsCapitalsourceId}
     , mcp_contractpartnerid = #{mcpContractpartnerId}
     , comment               = #{comment}
     , mpa_postingaccountid  = #{mpaPostingAccountId}
     , private               = #{privat}
 WHERE mau_userid = #{mauUserId}
   AND moneyflowid = #{id}

mapper.delete:
DELETE
  FROM moneyflows
 WHERE mau_userid = #{userId}
   AND moneyflowid = #{id}

mapper.select:
SELECT COALESCE(SUM(amount),0)
FROM vw_moneyflows
WHERE bookingdate BETWEEN #{validFrom} AND #{validTil}
AND mar_mau_userid = #{userId}
AND mcs_capitalsourceid IN

    #{mcsCapitalsourceId}

SELECT DISTINCT TO_CHAR(bookingdate, 'MM')
  FROM vw_moneyflows
 WHERE bookingdate BETWEEN #{beginOfYear} AND #{endOfYear}
   AND mar_mau_userid = #{userId}
   AND (private = 0 OR mau_userid = #{userId})
 ORDER BY 1

SELECT

FROM vw_moneyflows
WHERE bookingdate BETWEEN #{dateFrom} AND #{dateTil}
AND mar_mau_userid = #{userId}

SELECT 1
  FROM vw_moneyflows
 WHERE bookingdate BETWEEN #{dateFrom} AND #{dateTil}
   AND mar_mau_userid = #{userId}
   AND (private = 0 OR mau_userid = #{userId})
     FETCH FIRST 1 ROWS ONLY

SELECT MIN(bookingdate)
  FROM vw_moneyflows
 WHERE mar_mau_userid = #{userId}
   AND (private = 0 OR mau_userid = #{userId}
     )

SELECT MAX(bookingdate)
  FROM vw_moneyflows
 WHERE bookingdate      <  #{date}
   AND mar_mau_userid = #{userId}
   AND (private = 0 OR mau_userid = #{userId})

SELECT MIN(bookingdate)
  FROM vw_moneyflows
 WHERE bookingdate      >  #{date}
   AND mar_mau_userid = #{userId}
   AND (private = 0 OR mau_userid = #{userId})

SELECT MIN(mmf.invoicedate) date ,COALESCE(mse.mpa_postingaccountid, mmf.mpa_postingaccountid) mpaPostingAccountId ,SUM(COALESCE(mse.amount, mmf.amount)) amount FROM vw_moneyflows mmf LEFT OUTER JOIN moneyflowsplitentries mse ON mmf.moneyflowid = mse.mmf_moneyflowid WHERE mmf.invoicedate BETWEEN #{dateFrom} AND #{dateTil} AND COALESCE(mse.mpa_postingaccountid, mmf.mpa_postingaccountid) IN
#{postingAccountId}
AND mmf.mar_mau_userid = #{userId} AND ( mmf.private = 0 OR mmf.mau_userid = #{userId} ) GROUP BY to_char(mmf.invoicedate, 'YYYY') ,to_char(mmf.invoicedate, 'MM') ,mpaPostingAccountId ORDER BY 1 ,2 ,3

SELECT CONCAT(to_char(MIN(mmf.invoicedate), 'YYYY'),'-01-01') date ,COALESCE(mse.mpa_postingaccountid, mmf.mpa_postingaccountid) mpaPostingAccountId ,SUM(COALESCE(mse.amount, mmf.amount)) amount FROM vw_moneyflows mmf LEFT OUTER JOIN moneyflowsplitentries mse ON mmf.moneyflowid = mse.mmf_moneyflowid WHERE mmf.invoicedate BETWEEN #{dateFrom} AND #{dateTil} AND COALESCE(mse.mpa_postingaccountid, mmf.mpa_postingaccountid) IN
#{postingAccountId}
AND mmf.mar_mau_userid = #{userId} AND ( mmf.private = 0 OR mmf.mau_userid = #{userId} ) GROUP BY to_char(mmf.invoicedate, 'YYYY') ,mpaPostingAccountId ORDER BY 1 ,2 ,3

SELECT

FROM vw_moneyflows
WHERE bookingdate BETWEEN #{dateFrom} AND #{dateTil}
AND amount = CAST(#{amount} AS DECIMAL(8,2))
AND mar_mau_userid = #{userId}

SELECT

FROM vw_moneyflows
WHERE bookingdate BETWEEN #{dateFrom} AND #{dateTil}
AND ABS(amount) = CAST(#{amount} AS DECIMAL(8,2))
AND mar_mau_userid = #{userId}

SELECT

FROM vw_moneyflows
WHERE bookingdate BETWEEN #{dateFrom} AND #{dateTil}
AND mcs_capitalsourceid = #{capitalsourceId}
AND mar_mau_userid = #{userId}
AND ( private = 0 OR mau_userid = #{userId} )
