mapper.select:
SELECT DISTINCT "month"
  FROM vw_monthlysettlements
 WHERE mar_mau_userid = #{userId}
   AND "year" = #{year}
 ORDER BY "month"

SELECT

FROM vw_monthlysettlements
WHERE "month"= #{month}
AND "year" = #{year}
AND mar_mau_userid = #{userId}
ORDER BY CASE WHEN mau_userid = #{userId} THEN 1 ELSE 2 END
,mcs_capitalsourceid

SELECT 1
  FROM monthlysettlements
 WHERE mau_userid = #{userId}
   AND "year" = #{year}
   AND "month" = #{month}
     FETCH FIRST 1 ROWS ONLY

mapper.delete:
DELETE
  FROM monthlysettlements
 WHERE mau_userid = #{userId}
   AND "year" = #{year}
   AND "month" = #{month}

mapper.select:
SELECT

FROM vw_monthlysettlements
WHERE (
"year" > #{startYear} AND "year"    <   #{endYear}
OR "year" = #{startYear} AND "month"   >=  #{startMonth} AND #{startYear}  <  #{endYear}
OR "year" = #{endYear} AND "month"   <=  #{endMonth} AND #{startYear}  <  #{endYear}
OR "year" = #{startYear} AND "month" BETWEEN #{startMonth} AND #{endMonth} AND #{startYear} = #{endYear}
)
AND mcs_capitalsourceid IN

    #{mcsCapitalsourceId}

AND mar_mau_userid = #{userId}
ORDER BY "year"
,"month"
,CASE WHEN mau_userid = #{userId} THEN 1 ELSE 2 END
,mcs_capitalsourceid
