mapper.select:
SELECT 
  FROM settings
 WHERE mac_id = #{accessId}
   AND name = #{name}
 LIMIT 1

mapper.insert:
INSERT
  INTO settings
      (mac_id
      ,name
      ,`value`
      )
VALUES
      (#{accessId}
      ,#{name}
      ,#{value}
      )
    ON DUPLICATE KEY
UPDATE `value` = VALUES(`value`)
