mapper.select:
SELECT 
  FROM vw_contractpartners
 WHERE contractpartnerid = #{id}
   AND maf_id            = #{userId}
 LIMIT 1

SELECT 
  FROM vw_contractpartners
 WHERE maf_id    = #{userId}
   AND name      = #{name}
   AND NOW() BETWEEN maf_validfrom AND maf_validtil
 LIMIT 1

SELECT DISTINCT UPPER(SUBSTR(name,1,1)) letters
  FROM vw_contractpartners
 WHERE maf_id                       = #{userId}
   AND maf_validfrom  <=  #{validTil}
   AND maf_validtil   >=  #{validFrom}
   AND validfrom      <=  #{validTil}
   AND validtil       >=  #{validFrom}
 ORDER BY letters

SELECT COUNT(*)
  FROM vw_contractpartners
 WHERE maf_id                       = #{userId}
   AND maf_validfrom  <=  #{validTil}
   AND maf_validtil   >=  #{validFrom}
   AND validfrom      <=  #{validTil}
   AND validtil       >=  #{validFrom}

SELECT 
  FROM vw_contractpartners
 WHERE maf_id =                       #{userId}
   AND maf_validfrom  <=  #{validTil}
   AND maf_validtil   >=  #{validFrom}
   AND validfrom      <=  #{validTil}
   AND validtil       >=  #{validFrom}
 ORDER BY name

SELECT 
  FROM vw_contractpartners
 WHERE UPPER(name) LIKE CONCAT(UPPER(#{initial}),'%')
   AND maf_id = #{userId}
   AND maf_validfrom  <=  #{validTil}
   AND maf_validtil   >=  #{validFrom}
   AND validfrom  <=  #{validTil}
   AND validtil   >=  #{validFrom}
 ORDER BY name

mapper.update:
UPDATE contractpartners
   SET name                = #{name}
      ,street              = #{street}
      ,postcode            = #{postcode}
      ,town                = #{town}
      ,country             = #{country}
      ,validtil            = #{validTil}
      ,validfrom           = #{validFrom}
      ,mmf_comment          = #{mmfComment}
      ,mpa_postingaccountid = #{mpaPostingAccountId}
 WHERE contractpartnerid = #{id}
   AND mac_id_accessor   = #{macIdAccessor}
 LIMIT 1

mapper.delete:
DELETE
  FROM contractpartners
 WHERE mac_id_accessor   = #{groupId}
   AND contractpartnerid = #{id}
 LIMIT 1

mapper.select:
SELECT 1
  FROM moneyflows
 WHERE mcp_contractpartnerid = #{id}
   AND bookingdate NOT BETWEEN #{validFrom} AND #{validTil}
 LIMIT 1
