   AND maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )
 LIMIT 1

SELECT MAX(bookingdate)
  FROM vw_moneyflows
 WHERE maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )

SELECT MAX(bookingdate)
  FROM vw_moneyflows
 WHERE bookingdate          <  #{date}
   AND maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )

SELECT MIN(bookingdate)
  FROM vw_moneyflows
 WHERE bookingdate          >  #{date}
   AND maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )

SELECT MIN(bookingdate) date ,mpa_postingaccountid mpaPostingAccountId ,SUM(amount) amount FROM vw_moneyflows WHERE bookingdate BETWEEN #{dateFrom} AND #{dateTil} AND mpa_postingaccountid IN
#{postingAccountId}
AND amount < 0 AND maf_id = #{userId} AND ( private = 0 OR mac_id_creator = #{userId} ) GROUP BY YEAR(bookingdate) ,MONTH(bookingdate) ,mpa_postingaccountid ORDER BY 1 ,2 ,3

SELECT CONCAT(YEAR(MIN(bookingdate)),'-01-01') date ,mpa_postingaccountid mpaPostingAccountId ,SUM(amount) amount FROM vw_moneyflows WHERE bookingdate BETWEEN #{dateFrom} AND #{dateTil} AND mpa_postingaccountid IN
#{postingAccountId}
AND amount < 0 AND maf_id = #{userId} AND ( private = 0 OR mac_id_creator = #{userId} ) GROUP BY YEAR(bookingdate) ,mpa_postingaccountid ORDER BY 1 ,2 ,3

SELECT 
  FROM vw_moneyflows
 WHERE bookingdate         BETWEEN #{dateFrom} AND #{dateTil}
   AND amount              = CAST(#{amount} AS DECIMAL(8,2))
   AND maf_id              = #{userId}

SELECT 
  FROM vw_moneyflows
 WHERE bookingdate         BETWEEN #{dateFrom} AND #{dateTil}
   AND mcs_capitalsourceid = #{capitalsourceId}
   AND maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )

SELECT SUM(amount)								AS amount
      ,GROUP_CONCAT( DISTINCT comment ORDER BY comment DESC SEPARATOR ',')	AS comment

      ,YEAR(bookingdate)							AS year


      ,MONTH(bookingdate)							AS month


      ,mcp_contractpartnerid							AS contractpartnerid

  FROM vw_moneyflows
 WHERE 

               comment


               UPPER(comment)




               =


               REGEXP


               LIKE




               #{searchString}


                UPPER(#{searchString}) 



           AND

       bookingdate BETWEEN #{startDate} AND #{endDate}
   AND maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )

           AND amount  <  0


           AND mcp_contractpartnerid = #{contractpartnerId}


           AND mpa_postingaccountid = #{postingAccountId}

 GROUP BY

 	    ${groupBy1}


 	    ,


 	    ${groupBy2}
