mapper.select:
SELECT 
  FROM vw_moneyflows
 WHERE moneyflowid         = #{id}
   AND maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )
 LIMIT 1

mapper.update:
UPDATE moneyflows
   SET bookingdate           = #{bookingdate}
      ,invoicedate           = #{invoicedate}
      ,amount                = #{amount}
      ,mcs_capitalsourceid   = #{mcsCapitalsourceId}
      ,mcp_contractpartnerid = #{mcpContractpartnerId}
      ,comment               = #{comment}
      ,mpa_postingaccountid  = #{mpaPostingAccountId}
      ,private               = #{privat}
 WHERE mac_id_creator = #{macIdCreator}
   AND moneyflowid    = #{id}
 LIMIT 1

mapper.delete:
DELETE
  FROM moneyflows
 WHERE mac_id_creator = #{userId}
   AND moneyflowid    = #{id}
 LIMIT 1

mapper.select:
SELECT IFNULL(SUM(amount),0)
  FROM vw_moneyflows
 WHERE bookingdate   BETWEEN #{validFrom} AND #{validTil}
   AND maf_id              = #{userId}
   AND mcs_capitalsourceid IN 
   
        #{mcsCapitalsourceId}

SELECT DISTINCT YEAR(bookingdate) year
  FROM vw_moneyflows
 WHERE maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )
 ORDER BY year ASC

SELECT DISTINCT MONTH(bookingdate) month
  FROM vw_moneyflows
 WHERE bookingdate   BETWEEN #{beginOfYear} AND #{endOfYear}
   AND maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )
 ORDER BY month ASC

