SELECT MAX(bookingdate)
  FROM vw_moneyflows
 WHERE maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )

SELECT MAX(bookingdate)
  FROM vw_moneyflows
 WHERE bookingdate          <  #{date}
   AND maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )

SELECT MIN(bookingdate)
  FROM vw_moneyflows
 WHERE bookingdate          >  #{date}
   AND maf_id              = #{userId}
   AND (
        private            = 0
          OR
        mac_id_creator     = #{userId}
       )

SELECT STR_TO_DATE(DATE_FORMAT(MIN(bookingdate) ,'%Y-%m-01') ,'%Y-%m-%d') date ,mpa_postingaccountid mpaPostingAccountId ,SUM(amount) amount FROM vw_moneyflows WHERE bookingdate BETWEEN #{dateFrom} AND #{dateTil} AND mpa_postingaccountid IN
#{postingAccountId}
AND amount < 0 AND maf_id = #{userId} AND ( private = 0 OR mac_id_creator = #{userId} ) GROUP BY YEAR(bookingdate) ,MONTH(bookingdate) ,mpa_postingaccountid ORDER BY 1 ,2 ,3

SELECT STR_TO_DATE(DATE_FORMAT(MIN(bookingdate) ,'%Y-%m-01') ,'%Y-%m-%d') date ,mpa_postingaccountid mpaPostingAccountId ,SUM(amount) amount FROM vw_moneyflows WHERE bookingdate BETWEEN #{dateFrom} AND #{dateTil} AND mpa_postingaccountid IN
#{postingAccountId}
AND amount < 0 AND maf_id = #{userId} AND ( private = 0 OR mac_id_creator = #{userId} ) GROUP BY YEAR(bookingdate) ,mpa_postingaccountid ORDER BY 1 ,2 ,3
