mapper.select:
SELECT DISTINCT month
  FROM vw_monthlysettlements
 WHERE maf_id = #{userId}
   AND year   = #{year}
 ORDER BY month ASC

SELECT 
  FROM vw_monthlysettlements
 WHERE month  = #{month}
   AND year   = #{year}
   AND maf_id = #{userId}
 ORDER BY CASE WHEN mac_id_creator = #{userId} THEN 1 ELSE 2 END
      ,mcs_capitalsourceid

SELECT 1
  FROM monthlysettlements
 WHERE mac_id_creator = #{userId}
   AND year           = #{year}
   AND month          = #{month}
 LIMIT 1

mapper.insert:
INSERT
  INTO monthlysettlements
      (mac_id_creator
      ,mac_id_accessor
      ,mcs_capitalsourceid
      ,month
      ,year
      ,amount
      )
VALUES
      (#{macIdCreator}
      ,#{macIdAccessor}
      ,#{mcsCapitalsourceId}
      ,#{month}
      ,#{year}
      ,#{amount}
      )
    ON DUPLICATE KEY
UPDATE amount = VALUES(amount)

mapper.delete:
DELETE
  FROM monthlysettlements
 WHERE mac_id_creator = #{userId}
   AND year           = #{year}
   AND month          = #{month}
