mapper.update:
UPDATE access
   SET name                = #{name}
      ,perm_login          = #{permLogin}
      ,perm_admin          = #{permAdmin}
      ,att_change_password = #{attChangePassword}
 WHERE att_user = 1
   AND id       = #{id}
 LIMIT 1

UPDATE access
   SET password            = #{password}
      ,att_change_password = 0
WHERE att_user = 1
  AND id       = #{userId}
LIMIT 1

UPDATE access
   SET password            = #{password}
      ,att_change_password = 1
 WHERE att_user = 1
   AND id       = #{userId}
 LIMIT 1
